library(tidyverse)
library(dslabs)
pop_x &lt;- murders |&gt; 
    slice(1:6) |&gt;
    select(state, population)

elec_vote_y &lt;- results_us_election_2016 |&gt; 
    filter(state %in% c("Alabama", "Alaska", "Arizona", 
                        "California", "Connecticut", "Delaware")) |&gt; 
    select(state, electoral_votes) |&gt; 
    rename(elec_vote = electoral_votes)

